import React, { useState } from 'react';
import './Employees.css';

const Employees = () => {
  const [employees, setEmployees] = useState([
    { id: 1, name: 'deven', employeeCode: '1', intime: '9:00 AM', outtime: '5:00 PM' },
    { id: 2, name: 'aman', employeeCode: '2', intime: '8:30 AM', outtime: '4:30 PM' },
  ]);

  const [newEmployeeName, setNewEmployeeName] = useState('');
  const [newEmployeeCode, setNewEmployeeCode] = useState('');
  const [newEmployeeInTime, setNewEmployeeInTime] = useState('');
  const [newEmployeeOutTime, setNewEmployeeOutTime] = useState('');


  const addEmployee = () => {
    if (newEmployeeName && newEmployeeCode && newEmployeeInTime && newEmployeeOutTime) {
      const newEmployee = {
        id: employees.length + 1,
        name: newEmployeeName,
        employeeCode: newEmployeeCode,
        intime: newEmployeeInTime,
        outtime: newEmployeeOutTime,
      };

      setEmployees([...employees, newEmployee]);
      setNewEmployeeName('');
      setNewEmployeeCode('');
      setNewEmployeeInTime('');
      setNewEmployeeOutTime('');
    } else {
      alert('Please enter all details: Name, Employee Code, In-time, Out-time');
    }
  };

  return (
    <div>
      <h1>Employee List</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Employee Code</th>
            <th>In-time</th>
            <th>Out-time</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.id}</td>
              <td>{employee.name}</td>
              <td>{employee.employeeCode}</td>
              <td>{employee.intime}</td>
              <td>{employee.outtime}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Employees;
