// export default Employees;

import React, { useState } from 'react';

const Employees = () => {
  const [employees, setEmployees] = useState([
    { id: 1, name: 'deven', employeeCode: '1' },
    { id: 2, name: 'aman', employeeCode: '2' },
  ]);

  const [newEmployeeName, setNewEmployeeName] = useState('');
  const [newEmployeeCode, setNewEmployeeCode] = useState('');

  const handleNameChange = (e) => {
    setNewEmployeeName(e.target.value);
  };

  const handleCodeChange = (e) => {
    setNewEmployeeCode(e.target.value);
  };

  const addEmployee = () => {
    if (newEmployeeName && newEmployeeCode) {
      const newEmployee = {
        id: employees.length + 1,
        name: newEmployeeName,
        employeeCode: newEmployeeCode,
      };

      setEmployees([...employees, newEmployee]);
      setNewEmployeeName('');
      setNewEmployeeCode('');
    } else {
      alert('Please enter both name and employee code');
    }
  };

  return (
    <div>
      <h1>Employee List</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Employee Code</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((employee) => (
            <tr key={employee.id}>
              <td>{employee.id}</td>
              <td>{employee.name}</td>
              <td>{employee.employeeCode}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div>
        <h2>Add New Employee</h2>
        <input type="text"  placeholder="Enter name" value={newEmployeeName} onChange={handleNameChange} />
        <input type="text"  placeholder="Enter employee code" value={newEmployeeCode}
          onChange={handleCodeChange}
        />
        <button onClick={addEmployee}>Add Employee</button>
      </div>
    </div>
  );
};

export default Employees;
